/* eslint-disable @typescript-eslint/no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, useRef } from "react";
import { start } from "repl";
import useGameLogic from "./useGameLogic";

export default function useGame(render: () => void) {
  const gameLogic = useGameLogic();

  const [gameData, setGameData] = useState(gameLogic.gameDataRef.current);

  const renderFrequency = gameData.speed / 64;

  let time: number;
  const newFrame = useRef(-1);

  function loop() {
    if (time > newFrame.current) {
      time += 1000;
      console.log("test");
    }
  }

  useEffect(() => {
    time = Date.now();
  }, []);

  useEffect(() => {
    do {
      loop();
    } while (gameData.isRunning);
  }, []);

  return { ...gameLogic, gameData };
}
