import { getDefaultNormalizer } from "@testing-library/react";
import React, { ReducerWithoutAction, useState } from "react";

interface StartSiteProps {
  changePage: () => void;
  toggleSetting: (e) => void;
  changeDifficulty: () => void;
  settings: {
    audio: boolean;
    difficulty: string;
  };
}

export default function StartSite(props: StartSiteProps) {
  const [currentSite, setCurrentSite] = useState({
    menu: true,
    options: false,
    about: false,
    chooseGamemode: false,
  });

  function changeSite(e: React.MouseEvent) {
    setCurrentSite((previousCurrentSite) => ({
      menu: false,
      options: false,
      about: false,
      chooseGamemode: false,
      [(e.target as HTMLInputElement).id]: true,
    }));
  }

  return (
    <main>
      <h1 className="game-title" id="menu" onClick={(e) => changeSite(e)}>
        SNAKE
      </h1>
      {currentSite.menu && (
        <div className="menu">
          <button onClick={(e) => changeSite(e)} id="chooseGamemode">
            <p>START</p>
          </button>
          <button onClick={(e) => changeSite(e)} id="options">
            <p>OPTIONS</p>
          </button>
          <button onClick={(e) => changeSite(e)} id="about">
            <p>ABOUT</p>
          </button>
        </div>
      )}
      {currentSite.options && (
        <div>
          <div className="option-container">
            <h1>Audio</h1>
            <button onClick={(e) => props.toggleSetting(e)} id="audio">
              {props.settings.audio ? "ON" : "OFF"}
            </button>
          </div>
          <div className="option-container">
            <h1>Difficulty</h1>
            <button onClick={props.changeDifficulty}>
              {props.settings.difficulty}
            </button>
          </div>
          <button id="menu" onClick={(e) => changeSite(e)}>
            BACK
          </button>
        </div>
      )}
      {currentSite.about && (
        <div>
          <h1>About</h1>
          <button id="menu" onClick={(e) => changeSite(e)}>
            BACK
          </button>
        </div>
      )}
      {currentSite.chooseGamemode && (
        <div>
          <button>Classic Snake</button>
          <button onClick={props.changePage}>Bricks Snake</button>
        </div>
      )}
    </main>
  );
}
