/* eslint-disable @typescript-eslint/no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, useRef } from "react";
import useGameLogic from "./useGameLogic";

export default function useGame(render: () => void) {
  const gameLogic = useGameLogic();

  const [gameData, setGameData] = useState(gameLogic.gameDataRef.current);

  useEffect(() => {
    setGameData(gameLogic.gameDataRef.current);
  }, [
    gameLogic.gameDataRef.current.snakePosition,
    gameLogic.gameDataRef.current.isRunning,
  ]);

  useEffect(() => {
    if (gameData.isRunning) {
      const updateInterval = setInterval(() => {
        console.log("update");
        gameLogic.update();
      }, 1000);

      return () => {
        clearInterval(updateInterval);
      };
    }
  }, [gameData.isRunning]);

  useEffect(() => {
    const renderInterval = setInterval(() => {
      console.log("render");
      render();
    }, 100);
    return () => clearInterval(renderInterval);
  }, [gameData.snakePosition]);

  return { ...gameLogic, gameData };
}
