import React, { useEffect, useState } from "react";
import StartSite from "./components/StartSite";
import GameSite from "./components/GameSite";

export default function App() {
  const [isStartSite, setIsStartSite] = useState<boolean>(true);

  interface SettingsType {
    [key: string]: string | boolean;
  }

  const [settings, setSettings] = useState<SettingsType>({
    audio: true,
    difficulty: "EASY",
    gamemode: "",
  });

  function changeDifficulty() {
    const levels = [
      {
        name: "EASY",
        speedPerLevel: 6,
        bricksPerLevel: 4,
      },
      {
        name: "NORMAL",
        speedPerLevel: 12,
        bricksPerLevel: 5,
      },
      {
        name: "HARD",
        speedPerLevel: 18,
        bricksPerLevel: 6,
      },
    ];
    const currentLevel = levels.findIndex(
      (element) => element.difficulty === settings.difficulty
    );
    const nextLevel = levels[currentLevel + 1]
      ? levels[currentLevel + 1]
      : levels[0];
    setSettings((previousSettings) => ({
      ...previousSettings,
      difficulty: nextLevel,
    }));
  }

  function changeGamemode(selectedGamemode: string) {
    setSettings((previousSettings) => ({
      ...previousSettings,
      gamemode: selectedGamemode,
    }));
  }

  function toggleSetting(e: React.MouseEvent) {
    console.log((e.target as HTMLInputElement).name === "audio");

    setSettings((previousSettings) => ({
      ...previousSettings,
      [(e.target as HTMLInputElement).name]:
        !previousSettings[(e.target as HTMLInputElement).name],
    }));
  }

  function changePage() {
    setIsStartSite((previousPage) => !previousPage);
  }

  return (
    <div>
      {isStartSite && (
        <StartSite
          changePage={changePage}
          changeDifficulty={changeDifficulty}
          toggleSetting={toggleSetting}
          changeGamemode={changeGamemode}
          settings={settings}
        />
      )}
      {!isStartSite && <GameSite changePage={changePage} settings={settings} />}
    </div>
  );
}
