/* eslint-disable @typescript-eslint/no-unused-vars */
/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState, useRef } from "react";
import useGameLogic from "./useGameLogic";

export default function useGame(
  render: (oldSnakePosition: number[][], newSnakePosition: number[][]) => void
) {
  const requestID = useRef<number | null>(null);
  const previousTime = useRef<number | null>(null);

  const gameLogic = useGameLogic();

  const [gameData, setGameData] = useState(gameLogic.gameDataRef.current);
  const speed = gameData.speed;

  const oldSnakePosition = gameData.snakePosition;
  let currentlyDrawnSnakePosition = oldSnakePosition;

  const newUpdateFrameRef = useRef(-1);

  let counter = 0;

  function loop(time: number) {
    if (time > newUpdateFrameRef.current) {
      newUpdateFrameRef.current = time + speed;

      counter = 0;

      currentlyDrawnSnakePosition = oldSnakePosition;

      gameLogic.update();
    }
    setGameData({ ...gameLogic.gameDataRef.current });
    previousTime.current = time;

    requestID.current! = requestAnimationFrame(loop);
  }

  useEffect(() => {
    render(oldSnakePosition, gameData.snakePosition);
  }, [gameData]);

  useEffect(() => {
    requestID.current = requestAnimationFrame(loop);

    return () => cancelAnimationFrame(requestID.current!);
  }, []);
  return { ...gameLogic, gameData };
}
