import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Login() {
  const [userData, setUserData] = useState({
    name: "",
    password: "",
  });

  const [info, setInfo] = useState("");

  const navigate = useNavigate();

  function updateUserData(e: React.ChangeEvent<HTMLInputElement>) {
    setUserData((previousUserData) => ({
      ...previousUserData,
      [e.target.name]: e.target.value,
    }));
  }

  async function loginUser() {
    try {
      const response = await fetch("http://127.0.0.1:5500/", {
        method: "POST",
        body: JSON.stringify(userData),
        headers: {
          "Content-type": "application/json",
        },
      });
      const data = await response.json();
      setInfo(data.message);
      await localStorage.setItem("token", data.token);
      const sessionToken = await parseInt(
        `${localStorage.getItem("sessionToken")}`
      );
      sessionToken && navigate("/", { replace: true });
    } catch (error) {
      setInfo(JSON.stringify(error));
    }
  }

  return (
    <div className="user-login">
      <div className="login-container">
        <div className="login-inputs">
          <h1>Login</h1>
          <p>Name</p>
          <input name="name" onChange={(e) => updateUserData(e)}></input>

          <p>Password</p>
          <input name="password" onChange={(e) => updateUserData(e)}></input>
        </div>
        <button onClick={loginUser}>
          <p>LOGIN</p>
        </button>
      </div>
      <div className="login-down-text">
        <p>Don't have an account?</p>
        <Link to="/register">
          <p>&nbsp;Register</p>
        </Link>
      </div>
      <div className="login-down-text">
        {info && <p className="info">{info}</p>}
      </div>
    </div>
  );
}
